n = aviopen(SCI+'/contrib/sivp-svn-trunk/images/video.avi');
im = avireadframe(n);
fg = detectforeground(im, 'GMM');//or:fg = detectforeground(im). create background model

while ~isempty(im),
      fg = detectforeground(im);//get the foreground mask
      imshow(fg);   
      im = avireadframe(n);
end;

aviclose(n);
