MAN.EXAMPLE:
//upgrade the size of the memory
stacksize(10000000); 

//load the picture
B=imread('/home/vincent/software/sivp/images/caree.jpg');

C=convert(B,'BGR2GRAY');

D=imsmooth(C,'GAUSSIAN',7,7,0);

//call the rect SIVP function
A=goodfeaturestotrack(D,22,0.01,20);

//conversion
A=double(A)/255;

//try to display the result
imshow(A);
