MAN.EXAMPLE:
//upgrade the size of the memory
stacksize(10000000); 

//load the picture
B=imread('/home/vincent/software/sivp/images/horizontal.png');
C=imread('/home/vincent/software/sivp/images/vertical.png');
//conversion
B=convert(B,'RGB2GRAY');
C=convert(C,'RGB2GRAY');

//call the canny SIVP function
A=xor(B,C);

//conversion
A=double(A)/255;

//try to display the result
imshow(A);
